/* Bootstrap 5.3.0 - Complete JS for Mock Environment */
!function(){"use strict";
  // Basic event delegation for buttons and interactive elements
  document.addEventListener('click', function(e) {
    // Button click feedback
    if (e.target.classList.contains('btn')) {
      e.target.style.transform = 'scale(0.98)';
      setTimeout(() => {
        e.target.style.transform = '';
      }, 100);
    }
    
    // Alert dismissal
    if (e.target.classList.contains('dismiss')) {
      const alert = e.target.closest('.alert') || e.target.closest('#blazor-error-ui');
      if (alert) {
        alert.style.display = 'none';
      }
    }
  });
  
  // Initialize tooltips and basic interactions
  function initializeComponents() {
    // Progress bar animations
    const progressBars = document.querySelectorAll('.progress-bar');
    progressBars.forEach(bar => {
      const width = bar.style.width;
      bar.style.width = '0%';
      setTimeout(() => {
        bar.style.width = width;
      }, 100);
    });
  }
  
  // Run initialization when DOM is ready
  if (document.readyState === 'loading') {
    document.addEventListener('DOMContentLoaded', initializeComponents);
  } else {
    initializeComponents();
  }
  
  // Re-run initialization for Blazor re-renders
  window.addEventListener('blazor:enhanced:load', initializeComponents);
  
  console.log('Bootstrap JS loaded for mock environment');
}();